import React, { Component } from 'react';
import GlobalStyle from './styles/global';

import { Container, Content } from './styles';
import Logo from './components/Logo';
import Containercenter from './components/Containercenter';
import Containertext from './components/Containertext';
import Footer from './components/Footer';
import api from './services/api';

class App extends Component{
  state = {
    response: {},
    error: null,
    urlDigitada: ''
  };


  handleFileInfo = (e, urlDigitada) => {
    e.preventDefault();
    let data = ''
    api.post('/fileinfo',{
      url: 'https://www.youtube.com/watch?v=wDjeBNv6ip0'
    }).then((response) => {
       data = {
        id: response.data.videoId,
        title: response.data.title,
        thumbnail:response.data.thumbnail.thumbnails[0],
        url: response.data.video_url,
      }
      this.setState({
        response: data,
      })


    }).catch(function (error){
      data = true
      console.log(error);
    })




  };



  render(){
    const {response, urlDigitada} = this.state;
    return (
    <Container>

        <GlobalStyle />
        <Content>
          <Logo/>
          <Containercenter handleFileInfo={this.handleFileInfo(e => {urlDigitada})}/>
          <Containertext />
          <Footer />

        </Content>
    </Container>
    )
  }
}


export default App;
