  //   const body = await response.json();
  //   if (response.status !== 200) throw Error(body.message);

  //   return body;
  // };


  render(){
    const dados = this.state.response;
    console.log(dados)

    return (
    <Container>
        <GlobalStyle />
        <Content>
          <Logo /> 
          <Containercenter />
          <Containertext />
          <Footer />
        </Content>
    </Container>
    )
  }
}


export default App;
