import React, { Component } from 'react';
import GlobalStyle from './styles/global';

import { Container, Content } from './styles';
import Logo from './components/Logo';
import Containercenter from './components/Containercenter';
import Containertext from './components/Containertext';
import Footer from './components/Footer';
import api from './services/api';

class App extends Component{
  state = {
    response: {},
    error: null,
    urlDigitada: ''
  };


  handleFileInfo = (e) => {
    e.preventDefault();
    console.log(urlDigitada);
    let data = ''
    api.post('/fileinfo',{
      url:  'https://www.youtube.com/watch?v=J6sIK2KnhH8'
    }).then((response) => {
       data = {
        id: response.data.videoId,
        title: response.data.title,
        thumbnail:response.data.thumbnail.thumbnails[0],
        url: response.data.video_url,
      }
      this.setState({
        response: data,
      })
      console.log(data.title)

    }).catch( (e) => {
      this.setState({
        error: true
      })
      console.log(e);
    })

  };

  handleChange = (e) =>{
    const url = e.target.value

    this.setState({
      urlDigitada: url
    })
  }



  render(){
    const {response, urlDigitada} = this.state;
    return (
    <Container>
        <GlobalStyle />
        <Content>
          <Logo/>
          <Containercenter 
          handleChange={this.handleChange}
          handleFileInfo={this.handleFileInfo}
          urlDigitada={urlDigitada}
          />
          <Containertext />
          <Footer />

        </Content>
    </Container>
    )
  }
}


export default App;
